    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        try {
            int numero = Integer.parseInt(txtNumero.getText().trim());
            areaLog.setText(""); // limpiar √°rea
            areaLog.append("üîÅ Iniciando proceso iterativo...\n");
            areaLog.append("Sumando los n√∫meros del 1 al " + numero + "\n\n");
            
            int suma = 0;
            for (int i = 1; i <= numero; i++) {
                suma += i; 
                areaLog.append("Iteraci√≥n " + i + " ‚Üí suma parcial: " + suma + "\n");
            }

            areaLog.append("\n‚úÖ Resultado final: La suma de 1 hasta " + numero + " es " + suma + "\n");

        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this,
                    "Por favor ingrese un n√∫mero v√°lido.",
                    "Error de entrada", javax.swing.JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnCalcularActionPerformed

